<div x-data="{ 
    active: 'home',
    navBg: 'transparent',
    navText: 'text-white',
    menuOpen: false
}" 
x-init="
    window.addEventListener('scroll', () => {
        const scrollY = window.scrollY + 150;
        const sections = ['home', 'skill', 'project', 'contact'];

        for (let i = sections.length - 1; i >= 0; i--) {
            const section = document.getElementById(sections[i]);
            if (scrollY >= section.offsetTop) {
                active = sections[i];

                if (scrollY <= 100) {
                    navBg = 'bg-transparent';
                    navText = 'text-white';
                } else if (active === 'home') {
                    navBg = 'bg-white';
                    navText = 'text-[#630436]';
                } else {
                    navBg = 'bg-[#630436]';
                    navText = 'text-white';
                }

                break;
            }
        }

        if (window.scrollY === 0) {
            navBg = 'bg-transparent';
            navText = 'text-white';
        }
    });
"
>

    <!-- Navbar -->
    <div class="fixed top-0 left-0 right-0 z-50 transition duration-300 ease-in-out" :class="navBg">
        <div class="flex justify-between items-center px-6 py-4 text-lg font-bold transition-all duration-300" :class="navText">
            
            <!-- Hamburger (Mobile) -->
            <div class="lg:hidden">
                <button @click="menuOpen = !menuOpen" class="focus:outline-none">
                    <svg x-show="!menuOpen" xmlns="http://www.w3.org/2000/svg" class="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M4 6h16M4 12h16M4 18h16"/>
                    </svg>
                    <svg x-show="menuOpen" xmlns="http://www.w3.org/2000/svg" class="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M6 18L18 6M6 6l12 12"/>
                    </svg>
                </button>
            </div>

            <!-- Navigation Tabs -->
            <nav class="hidden lg:flex justify-center gap-8">
                <template x-for="tab in ['home', 'skill', 'project', 'contact']" :key="tab">
                    <a 
                        :href="`#${tab}`" 
                        @click="active = tab"
                        :class="active === tab ? 'border-b-2 border-current' : 'border-b-2 border-transparent'"
                        class="transition duration-200"
                        x-text="tab.toUpperCase()"
                    ></a>
                </template>
            </nav>

            <!-- Download CV + Login -->
            <div class="hidden lg:flex items-center gap-4">
                <a 
                    href="{{ asset('storage/Agena-CV.pdf') }}" 
                    download="Joshua_Agena_CV.pdf" 
                    class="block py-1"
                >
                    Download CV
                </a>
                @if (Route::has('login'))
                    <livewire:welcome.navigation />
                @endif
            </div>
        </div>

        <!-- Mobile Menu -->
        <div x-show="menuOpen" class="lg:hidden bg-white text-[#630436] px-6 pb-4 space-y-2 font-semibold">
            <template x-for="tab in ['home', 'skill', 'project', 'contact']" :key="tab">
                <a 
                    :href="`#${tab}`" 
                    @click="active = tab; menuOpen = false"
                    class="block border-b border-gray-300 py-1"
                    x-text="tab.toUpperCase()"
                ></a>
            </template> 
            <a 
                href="{{ asset('storage/Agena-CV.pdf') }}" 
                download="Joshua_Agena_CV.pdf" 
                class="block py-1"
            >
                Download CV
            </a>

                 
        </div>
    </div>

    <!-- Content Sections -->
    <section id="home" class="min-h-screen bg-[#630436] text-white flex flex-col items-center justify-center ">
        <livewire:welcome.home />
        <!-- Marquee -->
        
          <div class="w-full h-[100px] bg-[#111827] flex items-center text-white overflow-hidden relative z-50">
            <div class="marquee whitespace-nowrap flex gap-8 absolute animate-marquee">
              <!-- Repeated content for seamless loop -->
